
public class Pattern7 {

	public static void main(String[] args) {
		for (int i=1;i<=5;i++)
		{
			for(int j=1 ;j<=5;j++)
			{
			
			System.out.print(" ");
			}
			
			
		for (int k=5;k<=10;k++) 
		System.out.print("*");
		System.out.println();
		}
		
		// TODO Auto-generated method stub

	}

}
